    <style>
        .header {
            display: flex;
            align-items: center;
            flex-direction: column;
            margin: 0 auto 42px;
            font-size: 42px;
            font-weight: bold;
            color: lightgray;
        }
        .container {
            color: lightgray;
            line-height: 1.5;
        }
        h2 {
            font-size: 32px;
            font-weight: bold;
        }
        table tr td {
            padding: 4px 8px;
        }
    </style>
