<body class="antialiased">
<div class="relative sm:flex sm:justify-center sm:items-center min-h-screen bg-dots-darker bg-center bg-gray-100 dark:bg-dots-lighter dark:bg-gray-900 selection:bg-red-500 selection:text-white">

    <div class="max-w-7xl mx-auto p-6 lg:p-8">
        <div class="header">
            <svg height="48" width="48" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><linearGradient id="a" gradientUnits="userSpaceOnUse" x1="409.697485" x2="409.697485" y1="546.318868" y2="501.277222"><stop offset="0" stop-color="#3c309a"/><stop offset="1" stop-color="#6558cb"/></linearGradient><linearGradient id="b" gradientUnits="userSpaceOnUse" x1="23.290211" x2="23.516921" y1="45.565788" y2="1.92492"><stop offset="0" stop-color="#ececec"/><stop offset="1" stop-color="#fff"/></linearGradient><g transform="matrix(.95652174 0 0 .95652174 -366.807455 -477.024174)"><rect fill="url(#a)" height="45.041645" ry="22.520823" width="45.041668" x="386.0506" y="501.27719"/><path d="m24 2c-12.139409 0-22 9.860591-22 22s9.860591 22 22 22c12.139418 0 22-9.860591 22-22s-9.860582-22-22-22zm0 1.8339844c11.148758 0 20.166016 9.0172666 20.166016 20.1660156 0 11.148758-9.017258 20.166016-20.166016 20.166016-11.148749 0-20.1660156-9.017258-20.1660156-20.166016 0-.958967.0721133-1.901166.2011718-2.824219 2.1319162 1.791147 4.8736958 2.878907 7.8730468 2.878907 6.769018 0 12.263672-5.494674 12.263672-12.263672 0-2.9571894-1.058525-5.6623186-2.804687-7.7812504.862011-.1121464 1.739927-.1757812 2.632812-.1757812z" fill="url(#b)" stroke-width=".956522" transform="matrix(1.0454545 0 0 1.0454545 383.48052 498.70709)"/></g></svg>
            <h1>List of people with insomnia :)</h1>
        </div>

        <div class="container">
            <h2>User Information</h2>

            <div class="mb-3">
                <a href="/users/xml" target="_blank" class="btn btn-primary">Download XML</a>
            </div>

            <div class="mb-3">
                <label for="userCount" class="form-label">Number of Users:</label>
                <select id="userCount" class="form-select">
                    <option value="5">5</option>
                    <option value="10" selected>10</option>
                    <option value="15">15</option>
                </select>
            </div>

            <table class="table" id="usersTable">
                <thead>
                <tr>
                    <th><a href="#" class="sort" data-sortby="name">Name</a></th>
                    <th><a href="#" class="sort" data-sortby="phone">Phone</a></th>
                    <th><a href="#" class="sort" data-sortby="email">Email</a></th>
                    <th><a href="#" class="sort" data-sortby="country">Country</a></th>
                </tr>
                </thead>
                <tbody>
                <!-- User Data Will Be Appended Here by JavaScript -->
                </tbody>
            </table>
        </div>

        <script>
            let usersData = [];
            let sortDirection = {
                name: true,
                phone: true,
                email: true,
                country: true
            };

            document.addEventListener('DOMContentLoaded', function () {
                const loadUsers = (userCount = 10) => {
                    fetch(`/users/xml?count=${userCount}`)
                        .then(response => response.text())
                        .then(str => (new window.DOMParser()).parseFromString(str, "text/xml"))
                        .then(data => {
                            const users = data.querySelectorAll('user');
                            usersData = Array.from(users).map(user => ({
                                name: user.querySelector('name').textContent,
                                phone: user.querySelector('phone').textContent,
                                email: user.querySelector('email').textContent,
                                country: user.querySelector('country').textContent,
                            }));

                            renderUsers(usersData);
                        })
                        .catch(error => {
                            console.error(error);
                        });
                }

                const renderUsers = (users) => {
                    let usersHtml = '';
                    users.forEach(user => {
                        usersHtml += '<tr>' +
                            `<td>${user.name}</td>` +
                            `<td>${user.phone}</td>` +
                            `<td>${user.email}</td>` +
                            `<td>${user.country}</td>` +
                            '</tr>';
                    });
                    document.querySelector('#usersTable tbody').innerHTML = usersHtml;
                }

                const sortUsers = (sortBy) => {
                    usersData.sort((a, b) => {
                        if (a[sortBy] > b[sortBy]) return sortDirection[sortBy] ? 1 : -1;
                        if (a[sortBy] < b[sortBy]) return sortDirection[sortBy] ? -1 : 1;
                        return 0;
                    });

                    // Toggle the sorting direction for the next click
                    sortDirection[sortBy] = !sortDirection[sortBy];

                    renderUsers(usersData);
                }

                // Load users initially
                loadUsers();

                // Setup sort links
                document.querySelectorAll('.sort').forEach(sortLink => {
                    sortLink.addEventListener('click', (e) => {
                        e.preventDefault();
                        sortUsers(e.target.dataset.sortby);
                    });
                });

                // Setup user count selection
                document.querySelector('#userCount').addEventListener('change', (e) => {
                    loadUsers(e.target.value);
                });
            });
        </script>

        <div class="flex justify-center mt-16 px-0 sm:items-center sm:justify-between">
            <div class="text-center text-sm text-gray-500 dark:text-gray-400 sm:text-left">
                <div class="flex items-center gap-4">
                    <a href="https://github.com/sponsors/taylorotwell" class="group inline-flex items-center hover:text-gray-700 dark:hover:text-white focus:outline focus:outline-2 focus:rounded-sm focus:outline-red-500">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" class="-mt-px mr-1 w-5 h-5 stroke-gray-400 dark:stroke-gray-600 group-hover:stroke-gray-600 dark:group-hover:stroke-gray-400">
                            <path stroke-linecap="round" stroke-linejoin="round" d="M21 8.25c0-2.485-2.099-4.5-4.688-4.5-1.935 0-3.597 1.126-4.312 2.733-.715-1.607-2.377-2.733-4.313-2.733C5.1 3.75 3 5.765 3 8.25c0 7.22 9 12 9 12s9-4.78 9-12z" />
                        </svg>
                        Sponsor
                    </a>
                </div>
            </div>

            <div class="ml-4 text-center text-sm text-gray-500 dark:text-gray-400 sm:text-right sm:ml-0">
                Laravel v{{ Illuminate\Foundation\Application::VERSION }} (PHP v{{ PHP_VERSION }})
            </div>
        </div>
    </div>
</div>
</body>
</html>
